import React, { useState, useEffect } from "react";
import './Reservar.css';

const Reservar = () => {
  const [estadoHabitaciones, setEstadoHabitaciones] = useState({
    cardHabitacion1: "disponible",
    cardHabitacion2: "disponible",
    cardHabitacion3: "disponible",
    cardHabitacion4: "disponible",
  });

  useEffect(() => {
    const habitaciones = ["cardHabitacion1", "cardHabitacion2", "cardHabitacion3", "cardHabitacion4"];
    const estadoGuardado = {};
    habitaciones.forEach((habitacion) => {
      const estado = localStorage.getItem(habitacion) || "disponible";
      estadoGuardado[habitacion] = estado;
    });
    setEstadoHabitaciones(estadoGuardado);
  }, []);

  const mostrarFormularioReserva = (cardId) => {
    setEstadoHabitaciones((prev) => ({
      ...prev,
      [cardId]: prev[cardId] === "disponible" ? "reservando" : "disponible",
    }));
  };

  const confirmarReserva = (event, cardId) => {
    event.preventDefault();
    localStorage.setItem(cardId, "reservada");
    setEstadoHabitaciones((prev) => ({
      ...prev,
      [cardId]: "reservada",
    }));
  };

  const renderFormulario = (cardId) => {
    return (
      <div className="formularioReserva mt-3" style={{ display: estadoHabitaciones[cardId] === "reservando" ? "block" : "none" }}>
        <form onSubmit={(e) => confirmarReserva(e, cardId)}>
          <label htmlFor="nombre">Nombres:</label>
          <input type="text" className="form-control" id="nombre" name="nombre" required />

          <label htmlFor="apellido">Apellidos:</label>
          <input type="text" className="form-control" id="apellido" name="apellido" required />

          <label htmlFor="documento">Documento:</label>
          <input type="text" className="form-control" id="documento" name="documento" required />

          <label htmlFor="fechaEntrada">Fecha de Ingreso:</label>
          <input type="date" className="form-control" id="fechaEntrada" name="fechaEntrada" required />

          <label htmlFor="fechaSalida">Fecha de Salida:</label>
          <input type="date" className="form-control" id="fechaSalida" name="fechaSalida" required />

          <button type="submit" className="btn btn-primary mt-2">Confirmar Reserva</button>
        </form>
      </div>
    );
  };

  const habitaciones = [
    {
      id: "cardHabitacion1",
      titulo: "Habitación Individual",
      descripcion:
        "Perfecta para quienes viajan solos. Cuenta con una cama individual, ideal para descansar después de un día de exploración o trabajo. Incluye televisión, Wi-Fi de alta velocidad, escritorio y baño privado.",
      capacidad: "1 persona",
      precio: "$150.000 COP",
      imagen: "images/Individual.png",
    },
    {
      id: "cardHabitacion2",
      titulo: "Habitación Doble",
      descripcion:
        "Ideal para amigos o colegas que deseen compartir. Dispone de dos camas individuales, además de televisión, Wi-Fi, escritorio y un baño completo. Un ambiente moderno y acogedor.",
      capacidad: "2 personas",
      precio: "$200.000 COP",
      imagen: "images/Doble.png",
    },
    {
      id: "cardHabitacion3",
      titulo: "Habitación Matrimonial",
      descripcion:
        "Diseñada para parejas, cuenta con una cama matrimonial perfecta para una escapada romántica o una estadía relajante en pareja. Dispone de TV, Wi-Fi, escritorio y un baño privado completo.",
      capacidad: "2 personas",
      precio: "$250.000 COP",
      imagen: "images/Matrimonial.png",
    },
    {
      id: "cardHabitacion4",
      titulo: "Habitación Familiar",
      descripcion:
        "Pensada para familias o grupos de hasta cinco personas. Cuenta con dos camas y un sofá cama. Además, incluye TV, Wi-Fi y un baño amplio, brindando una experiencia cómoda y divertida para todos los miembros de la familia.",
      capacidad: "2-5 personas",
      precio: "$350.000 COP",
      imagen: "images/Familiar.png",
    },
  ];

  return (
    <div>
      <nav className="navbar navbar-expand-lg bg-body-tertiary">
        <div className="container-fluid">
          <img src="images/logoTerraNova.png" style={{ width: "100%", maxWidth: "80px" }} className="navbar-brand" alt="Logo de Hotel Terra Nova" />
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarScroll" aria-controls="navbarScroll" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarScroll">
            <ul className="navbar-nav me-auto my-2 my-lg-0 navbar-nav-scroll" style={{ "--bs-scroll-height": "100px" }}>
              <li className="nav-item">
                <a className="nav-link active" aria-current="page" href="index.html">Inicio</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">Reservar</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="nosotros.html">Nosotros</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="contacto.html">Contáctanos</a>
              </li>
            </ul>
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <a className="nav-link admin-link" href="login.html">Administrador</a>
              </li>
            </ul>
            <form className="d-flex" role="search">
              <input className="form-control me-2" type="search" placeholder="Buscar" aria-label="Search" />
              <button className="btn btn-primary" type="submit">Buscar</button>
            </form>
          </div>
        </div>
      </nav>

      <main>
        {habitaciones.map((habitacion) => (
          <div className="card mb-3" key={habitacion.id} id={habitacion.id}>
            <div className="row g-0">
              <div className="col-md-4">
                <img src={habitacion.imagen} className="img-fluid rounded-start" alt={habitacion.titulo} />
              </div>
              <div className="col-md-8">
                <div className="card-body">
                  <h5 className="card-title">{habitacion.titulo}</h5>
                  <p className="card-text">{habitacion.descripcion}</p>
                  <p className="card-text">
                    <small className="text-body-secondary">
                      Capacidad: {habitacion.capacidad}
                      <br />
                      Valor por noche: {habitacion.precio}
                    </small>
                  </p>
                  <p className={`estado-habitacion ${estadoHabitaciones[habitacion.id] === "disponible" ? "text-success" : "text-danger"}`}>
                    {estadoHabitaciones[habitacion.id] === "disponible" ? "Disponible" : "Reservada"}
                  </p>
                  <button
                    type="button"
                    className={`btn ${estadoHabitaciones[habitacion.id] === "disponible" ? "btn-success" : "btn-danger"}`}
                    onClick={() => mostrarFormularioReserva(habitacion.id)}
                  >
                    {estadoHabitaciones[habitacion.id] === "disponible" ? "Reservar" : "No Disponible"}
                  </button>
                  {renderFormulario(habitacion.id)}
                </div>
              </div>
            </div>
          </div>
        ))}
      </main>
    </div>
  );
};

export default Reservar;
